	public void update(int delta) throws SlickException {
		angle = getController().getAngle();
		angle = (float) (angle%Math.PI*2);
		angle = (float) ((angle + Math.PI*2) % Math.PI*2);
		if (angle > Math.PI)
			angle -= Math.PI*2;
		
		if (spawns.size() > 0 && spawns.size() > spawncap) {
			spawns.get(0).onDeath();
		}
		if (stats.getCurrentHealth() <= 0) {
			onDeath();
			return;
		}
		if (o != null && linger == false) {
			if (o.destroy == true)
				this.destroy = true;
		}
		if (requireForce == true && force == false) {
			return;
		}
		if (active == true || force == true) {
			if (size != lSize) {
				init();
				lSize = size;
			}
			
			if (!overrideMove && controller != null)
				controller.move(delta);
			updateHitboxes();
			if (b.size() > 0) {
				for (int i = b.size()-1; i > -1; i--) {
					Entity e = b.get(i);
					if (e.destroy == true) {
						b.remove(e);
						b.trimToSize();
						continue;
					} else if (!MainGame.getEntityManager().getBullets().contains(e))
						MainGame.getEntityManager().addBullet(e);
				}
			}
			if (spawns.size() > 0) {
				for (int i = spawns.size()-1; i > -1; i--) {
					Entity e = spawns.get(i);
					if (e.destroy == true) {
						spawns.remove(e);
						spawns.trimToSize();
						continue;
					}
					if (!MainGame.getEntities().contains(e) && e.destroy != true)
						MainGame.getEntities().add(e);
					if (e.team != this.team)
						e.team = this.team;
				}
			}
			if (followers.size() > 0) {
				for (int i = 0; i < followers.size(); i++) {
					Entity e = followers.get(i);
					if (e.destroy == true) {
						followers.remove(e);
						i--;
						continue;
					}
					if (e.team != this.team)
						e.team = this.team;
					if (!MainGame.getEntities().contains(e) && e.destroy != true)
						MainGame.getEntities().add(e);
					e.forceUpdate(delta);
				}
			}
			if (sem != null)
				sem.update(delta);
			cooldown-=delta;
		} else {
			return;
		}
		force = false;
	}
	
	public void forceUpdate(int delta) throws SlickException {
		force = true;
		update(delta);
	}
	
	public void updateHitboxes() {
		if (hitbox instanceof Rectangle && wOverride >= 0  && hitbox.getWidth() != wOverride)
			((Rectangle)hitbox).setWidth(wOverride);
		if ( hitbox instanceof Rectangle && hOverride >= 0 && hitbox.getHeight() != hOverride)
			((Rectangle)hitbox).setHeight(hOverride);
		if (collisionbox != null) {
			collisionbox.setCenterX(getLocation().getX());
			collisionbox.setY(getLocation().getY()-posMod.y-(collisionbox.getHeight()));	
		}
		if (hitbox != null) {
			hitbox.setCenterX(getLocation().getX());
			hitbox.setY(getLocation().getY()-posMod.y-(hitbox.getHeight()));	
		}
	}
	
	public void onDeath() throws SlickException {
		if (natural && MainGame.mm.natspawns > 0) {
			MainGame.mm.natspawns--;
		}
		for (Entity i : spawns) {
			if (i.linger == false) {
				i.destroy = true;
			}
		}
		destroy = true;
	}
