	/**
	 * Action taken when hit by a bullet
	 * @param b Bullet Bullet that hit
	 */
	public void onHitByBullet(Bullet b) {
		//Checks if the entity is active
		if (active == false)
			return;
		//Makes sure the bullet is valid
		if (b == null)
			return;
		//Get the origin of the bullet
		Entity origin = b.getOrigin();
		//Makes sure the origin is not on the same team or this entity or if this entity is invincible
		if (origin.getTeam() == this.getTeam() || origin == this || invincible == true)
			return;
		//Damage dealt
		int damage = 0;
		//Adds effect if applicable 
		if (sem != null && b.getEffect() != null)
			sem.addEffect(b.getEffect());
		//Preforms damage calculation based on whether or not the bullet damage scales with attack stat
		if (b.scalesWithAtk()) {
			if (b.getBaseDamage()[0] >= b.getBaseDamage()[1]) {
				damage =(int) ((b.getBaseDamage()[0]*(0.5f+(origin.getAttack()/50f))));
			} else {
				damage =(int) ((b.getBaseDamage()[0]+r.nextInt(b.getBaseDamage()[1]-b.getBaseDamage()[0]))*(0.5f+(origin.getAttack()/50f)));
			}
		} else {
			if (b.getBaseDamage()[0] >= b.getBaseDamage()[1]) {
				damage =(int) ((b.getBaseDamage()[0]));
			} else {
				damage =(int) ((b.getBaseDamage()[0]+r.nextInt(b.getBaseDamage()[1]-b.getBaseDamage()[0])));
			}
		}
		//Sets damage to 0 if invulnerable
		if (invulnerable == true)
			damage = 0;
		else {
			//Does damage and applies on Hit Effect
			b.getOrigin().onAttackHit(b, damage);
			b.onHit();
		}
		onHit(damage, b.getOrigin(), b.ignoresArmor());
	}
	
	/**
	 * Shoots a bullet
	 * @param angle float Bullet angle in radians
	 * @param force
	 * @return boolean Whether or not the action succeeded
	 * @throws SlickException
	 */
	public boolean shootBullet(float angle, boolean force) throws SlickException {
		if (firing == false) {
			firing = true;	
		}
		if (cooldown <= 0 || force) {
			b.add(new Bullet("Bob", getController().getLoc().x, getController().getLoc().y, 350, angle, 50, 30, 50,this));
			float SPS = 2.5f + 6.5f*((2*getDexterity())/75f);
			cooldown = 1000/SPS;
			firing = false;
			return true;
		}
		return false;
	}
	
